  KEY `idCargo` (`idCargo`),
  CONSTRAINT `Usuario_ibfk_1` FOREIGN KEY (`idCargo`) REFERENCES `Cargo` (`idCargo`)
) ENGINE=InnoDB AUTO_INCREMENT=82 DEFAULT CHARSET=utf8;


